import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from 'src/app/Services/product-service.service';
import { Applicationtype } from 'src/app/data/applicationtype';
import { Product } from 'src/app/data/product';
import { UserserviceService } from '../../Services/userservices.service';
import { Category } from '../../data/category';

@Component({
  selector: 'app-create-product',
  templateUrl: './create-product.component.html',
  styleUrls: ['./create-product.component.css']
})
export class CreateProductComponent implements OnInit {
  private readonly assetsFolderPath = 'assets/products/';
  public productForm: any;
  public id: number = 0;
  public imagePathList: string[] = [];
  public Category: Category[] = [];
  public application: Applicationtype[] = [];
  public product: Product | undefined;
  public productname?: string = "";
  public productlist: Product[] = [];
  loading: boolean = false;
  public url: string = '';

  constructor(
    private formBuilder: FormBuilder,
    private productServiceService: ProductServiceService,
    public router: Router,
    public userserviceService: UserserviceService,
    public route: ActivatedRoute
  ) {
    if (!userserviceService.getuserloginstatus() && !userserviceService.isadmin) {
      router.navigate(['/Login']);
    }
    this.route.paramMap.subscribe(params => {
      this.id = Number(params.get('id'));
      console.log(this.id);
    });
    if (this.id !== 0) {
      this.product = this.productServiceService.getproductId(this.id);
      console.log(this.product);
      if (this.product !== undefined) {
        //this.applicationname=this.application.ApplicationName;
      }
    }
    this.productlist = productServiceService.getProduct();
    this.Category = productServiceService.getcategory();
    this.application = productServiceService.getapplication();
  }

  ngOnInit(): void {
    this.productForm = this.formBuilder.group({
      name: [this.product?.Name, Validators.required],
      price: [this.product?.Price, [Validators.required, Validators.min(1), Validators.max(2147483647)]],
      shortDesc: [this.product?.Shortdescription],
      description: [this.product?.Description],
      categoryId: [this.product?.CategoryId, Validators.required],
      applicationTypeId: [this.product?.ApplicationId, Validators.required]
    });
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (event: ProgressEvent<FileReader>) => {
      this.url = event.target?.result as string;
      console.log(this.url);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  }

  async onSubmit() {
    if (this.productForm.valid) {
      const productName = this.productForm.controls['name'].value;
      const isDuplicate = this.productlist.some(
        product => product.Name?.toLowerCase() === productName.toLowerCase()
      );

      if (isDuplicate) {
        alert('Duplicate product name not allowed.');
      } else {
        try {
          this.productServiceService.Addproduct(
            new Product(
              this.id,
              productName,
              this.productForm.controls['shortDesc'].value,
              this.productForm.controls['price'].value,
              this.url,
              this.productForm.controls['categoryId'].value,
              this.productForm.controls['applicationTypeId'].value,
              this.productForm.controls['description'].value
            )
          );

          console.log('Image uploaded successfully');
          this.router.navigate(['/admin/displayproductlist']);
        } catch (error) {
          console.error('Error uploading image:', error);
          // Handle the error if required
        }
      }
    }
  }
}
<div class="container m-4">
  <form [formGroup]="productForm" (ngSubmit)="onSubmit()">
    <div class="border p-3">
      <div class="form-group row">
        <h2 class="text-center text-info pl-3">Create Product</h2>
      </div>
      <div class="row">
        <div class="col-8">
          <div class="form-group row">
            <div class="col-4">
              <p class="text-danger"></p>
            </div>
            <div class="col-8"></div>
            <div class="col-4">
              <label for="Product_Name">Name</label>
            </div>
            <div class="col-8">
              <input class="form-control" type="text" id="Product_Name" formControlName="name" />
              <span class="text-danger field-validation-valid" *ngIf="productForm.get('name')?.invalid && (productForm.get('name')?.dirty || productForm.get('name')?.touched)">
                The Name field is required.
              </span>
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-4">
              <label for="Product_Price">Price</label>
            </div>
            <div class="col-8">
              <input class="form-control" type="text" id="Product_Price" formControlName="price" />
              <span class="text-danger field-validation-valid" *ngIf="productForm.get('price')?.invalid && (productForm.get('price')?.dirty || productForm.get('price')?.touched)">
                The field price must be a number between 1 and 2147483647.
              </span>
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-4">
              <label for="Product_ShortDesc">ShortDesc</label>
            </div>
            <div class="col-8">
              <input class="form-control" type="text" id="Product_ShortDesc" formControlName="shortDesc" />
            </div>
          </div>
          <br />

          <div class="form-group row">
            <div class="col-4">
              <label for="Product_Description">Description</label>
            </div>
            <div class="col-8">
              <textarea class="form-control summernote" id="Product_Description" formControlName="description"></textarea>
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-4">Image</div>
            <div class="col-8">
              <input type="file" (change)="onFileChange($event)" name="files" id="uploadBox" multiple class="form-control" />
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-4">
              <label for="Product_CategoryId">Category Type</label>
            </div>
            <div class="col-8">
              <select class="form-control" id="Product_CategoryId" formControlName="categoryId">
                <option disabled selected>--Select Category--</option>
                <option *ngFor="let cat of Category" [value]="cat.Id">{{ cat.CategoryName }}</option>
              </select>
              <span class="text-danger field-validation-valid" *ngIf="productForm.get('categoryId')?.invalid && (productForm.get('categoryId')?.dirty || productForm.get('categoryId')?.touched)">
                The Category Type field is required.
              </span>
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-4">
              <label for="Product_ApplicationTypeId">Application Type</label>
            </div>
            <div class="col-8">
              <select class="form-control" id="Product_ApplicationTypeId" formControlName="applicationTypeId">
                <option disabled selected>--Select Application Type--</option>
                <option *ngFor="let app of application" [value]="app.Id">{{ app.ApplicationName }}</option>
              </select>
              <span class="text-danger field-validation-valid" *ngIf="productForm.get('applicationTypeId')?.invalid && (productForm.get('applicationTypeId')?.dirty || productForm.get('applicationTypeId')?.touched)">
                The Application Type field is required.
              </span>
            </div>
          </div>
          <br />
          <div class="form-group row">
            <div class="col-8 offset-4 row">
              <div class="col">
                <input type="submit" [disabled]="productForm.invalid" class="btn btn-primary w-100" value="Create" />
              </div>
              <div class="col">
                <a class="btn btn-success w-100" [routerLink]="'/admin/displayproductlist'">
                  <i class="fas fa-sign-out-alt"></i> Back
                </a>
              </div>
            </div>
          </div>
        </div>
        <div class="col-4"></div>
      </div>
    </div>
  </form>
</div>
