import { Component } from '@angular/core';
import { ActivatedRoute, Router, Route } from '@angular/router';
import { Subscription } from 'rxjs/internal/Subscription';
import { ProductServiceService } from '../../Services/product-service.service';
import { NgModel } from '@angular/forms';
import { UserserviceService } from '../../Services/userservices.service';
import { Product } from 'src/app/data/product';
import { Cart } from 'src/app/data/cart';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent {
  private id:number = 0;
  private userid:number=0;
  public loginstatus:boolean = false;
  //public userid?:number=0;
  private productdata:Product[] = [];
  public product:any;
  public status:boolean = false;
  private application:any;
  private cart:Cart[]=[];
  private category:any;
  private tempcart?:Cart;
  constructor(private ProductServiceService:ProductServiceService,public useServices:UserserviceService, public route:ActivatedRoute,private router:Router,){
    if(!useServices.getuserloginstatus()){
      router.navigate(['/Login']);
    } 
    this.route.paramMap.subscribe(params =>{
      this.id=Number( params.get('id'));
    })
     this.loginstatus=useServices.getuserloginstatus();
    this.userid=useServices.getuserloginid();
    this.productdata=ProductServiceService.getProduct();
    this.application=ProductServiceService.getapplication();
    this.category=ProductServiceService.getcategory();
    this.product=this.productdata.find((product: any) => product.Id === this.id);
    this.cart=this.useServices.getcart();

    this.tempcart=this.cart.find(c=>c.ProductiD==this.product.Id && c.UserId==this.userid);
    if(this.tempcart==undefined){
      this.status=true;
    }







  }
  Addtocart(id?:number) {
    console.log("inside");
    this.useServices.addcart(new Cart(0,this.userid,id))
    this.router.navigate(['/']);
  }
  removetocart(id?:number){
    if(id!==undefined)
    this.useServices.removecarts(this.userid,id);
    this.router.navigate(['/']);

  }


}

<div class="container backgroundWhite pt-2 mt-5 " style="margin-bottom:8%;"  >
    <div class="card" style="border:1px solid #000000; ">
        <div class="card-header bg-dark text-light m-0 row container" style="border-radius: 0px;">
            <div class="col-12 col-md-6">
                <h1 class="text-white">{{product.Name}}</h1>
            </div>
            <div class="col-12 col-md-6 text-md-end">
                <h1 class="text-warning">
                    {{product.Price}}/sqft
                </h1>
            </div>
        </div>
        <div class="card-body">
            <div class="container rounded p-2">
                <div class="row">
                    <div class="col-12 col-lg-4 p-1 text-center">
                        <img style="width:295px; height:196px;" src={{product.Image}} width="100%" class="rounded" />
                    </div>
                    <div class="col-12 col-lg-8">
                        <div class="row pl-3">

                            <div class="col-12">
                                <span class="badge p-3 border" style="background-color:rgb(156, 58, 90)">Emperador</span>
                                <span class="badge p-3 border" style="background-color:rgb(70, 141, 141)">Flooring</span>
                                <h3 class="text-success"></h3>

                                <p class="text-secondary">{{product.Shortdescription}} </p>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div class="card-footer bg-dark">
            <div class="col">
                <div class="col-12 col-md-6 pb-1 ">
                    <a  [routerLink]="['/']" class="col-6 btn btn-success btn-square form-control btn-lg" style="height:50px;">Back to List</a>
                </div>
                <span *ngIf="useServices.getuserloginstatus()&& !useServices.isadmin ">
                <div *ngIf="loginstatus && status" class="col-12 col-md-6 pb-1 ">
                    <a  class="col-6 btn btn-info form-control btn-lg" style="height:50px;" (click)="Addtocart(product.Id)">Add to Inquiry</a>
                </div>
                <div *ngIf="loginstatus && !status" class="col-12 col-md-6 pb-1 ">
                    <a  class="btn btn-warning btn-square form-control btn-lg" style="height:50px;" (click)="removetocart(product.Id)">Remove to Inquiry</a>
                </div>
            </span>
            </div>
        </div>
    </div>
</div>
