import { Component } from '@angular/core';
import{FormControl,FormGroup} from '@angular/forms';
import { User } from 'src/app/data/user';
import { UserserviceService } from 'src/app/Services/userservices.service';
import { combineLatest } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {  
  loguser: User[] = [];
  data: User[]=[];
constructor(private userservices:UserserviceService,private UserserviceService: UserserviceService,public router:Router){
  this.UserserviceService.updateshowstatus(false);
  this.data=this.UserserviceService.getUData();

}
  loginform=new FormGroup({

    username: new FormControl(''),
    password:new FormControl(''),


  })
  Loginuser(){
    console.log(this.loginform.value)
    console.log(this.data);
    const user= this.data.find(u=>u.username==this.loginform.get('username')?.value &&  u.password==this.loginform.get('password')?.value);
    console.log(user)
    if(user) {
      console.log("success");
      if(user.Id!=undefined)
      this.userservices.updatelofingstatus(true,user.Id);
      this.router.navigate(['/']);


    }
    else{
      console.log("fail");
    }


  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Login Form</title>
  <!-- Bootstrap CSS -->
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.0.1/css/bootstrap.min.css">
  <style>
    /* Custom styles */
    body {
      background-color: #f8f9fa;
    }

    .bg-img {
      background-image: url("/src/assets/Statuary\ White.png"); /* Replace with your image path */
      background-size: cover;
      background-position: center;
      height: 100vh;
    }

    .login-container {
      max-width: 400px;
      margin: 0 auto;
      margin-top: 100px;
      background-color: rgba(255, 255, 255, 0.9);
      padding: 20px;
      border-radius: 10px;
      border: 2px solid #17a2b8;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }
  </style>
</head>
<body>
  <div class="bg-img">
    <div class="container d-flex justify-content-center align-items-center vh-100">
      <!-- Center the form vertically and horizontally -->
      <div class="col-md-5">
        <h1 class="text-center mb-4">Login</h1>
        <hr class="mb-4"/>
        <form [formGroup]="loginform" (ngSubmit)="Loginuser()">
          <div class="form-group">
            <label for="username">Username:</label>
            <input type="email" class="form-control" formControlName="username" name="username" placeholder="Enter your username">
          </div>

          <div class="form-group mt-3">
            <label for="password">Password:</label>
            <input type="password" class="form-control" formControlName="password" name="password" placeholder="Enter your password">
          </div>

          <div class="d-grid gap-2 mt-4">
            <button type="submit" class="btn btn-primary btn-block">Login</button> <!-- Center the button -->
          </div>
        </form>
      </div>
    </div>
  </div>

  <!-- Bootstrap JS (optional) -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.0.1/js/bootstrap.bundle.min.js"></script>
</body>
</html>
