import { Component } from '@angular/core';
import{FormControl,FormGroup} from '@angular/forms';
import { User } from 'src/app/data/user';
import { UserserviceService } from 'src/app/Services/userservices.service';
import { combineLatest } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {  
  loguser: User[] = [];
  data: User[]=[];
constructor(private userservices:UserserviceService,private UserserviceService: UserserviceService,public router:Router){
  this.UserserviceService.updateshowstatus(false);
  this.data=this.UserserviceService.getUData();

}
  loginform=new FormGroup({

    username: new FormControl(''),
    password:new FormControl(''),


  })
  Loginuser(){
    console.log(this.loginform.value)
    console.log(this.data);
    const user= this.data.find(u=>u.username==this.loginform.get('username')?.value &&  u.password==this.loginform.get('password')?.value);
    console.log(user)
    if(user) {
      console.log("success");
      if(user.Id!=undefined)
      this.userservices.updatelofingstatus(true,user.Id);
      this.router.navigate(['/']);


    }
    else{
      console.log("fail");
    }


  }
}
