  styleUrls: ['./alert.component.css']
})
export class AlertComponent implements OnInit {
  @Input() type: string='';
  @Output() onClose = new EventEmitter<any>;
  constructor(){}
  ngOnInit(): void {

  }
  close(){
    this.onClose.emit();
  }

}
