import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { User } from 'src/data/user';
import { UserserviceService } from 'src/app/Services/userservices.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  newUser: any;

  constructor(public dataservice: UserserviceService) {}

  registerForm = new FormGroup({
    name: new FormControl(''),
    email: new FormControl(''),
    phone: new FormControl(''),
    password: new FormControl(''),
    confirmPassword: new FormControl('')
  });

  onSubmit() {
    const name: string = this.registerForm.get('name')?.value ?? '';
    const email: string = this.registerForm.get('email')?.value ?? '';
    const phone: string = this.registerForm.get('phone')?.value ?? '';
    const password: string = this.registerForm.get('password')?.value ?? '';
    const confirmPassword: string = this.registerForm.get('confirmPassword')?.value ?? '';

    this.newUser = new User(name, email, phone, password);
    console.log(this.registerForm.value);
    this.dataservice.addUser(this.newUser);
    console.log(this.dataservice.getUData());
  }
}
<!-- register.component.html -->
<br>
<br>
<div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <h1 class="text-center">Register</h1>
        <hr/>
<form [formGroup]="registerForm" (ngSubmit)="onSubmit()" class="container">
    <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" id="name" formControlName="name" class="form-control" required>
    </div>

    <div class="form-group">
        <label for="email">Email:</label>
        <input type="email" id="email" formControlName="email" class="form-control" required>
    </div>

    <div class="form-group">
        <label for="phone">Phone Number:</label>
        <input type="tel" id="phone" formControlName="phone" class="form-control" required>
    </div>

    <div class="form-group">
        <label for="password">Password:</label>
        <input type="password" id="password" formControlName="password" class="form-control" required>
    </div>

    <div class="form-group">
        <label for="confirmPassword">Confirm Password:</label>
        <input type="password" id="confirmPassword" formControlName="confirmPassword" class="form-control" required>
    </div>
    <div class="text-center">
        <button type="submit" class="btn btn-primary col-4 mt-4 text-center">Register</button>
    </div>

</form>
</div>
</div></div>
