import { Component } from '@angular/core';
import{FormControl,FormGroup} from '@angular/forms';
import { User } from 'src/data/user';
import { UserserviceService } from 'src/app/Services/userservices.service';
import { combineLatest } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {  
  loguser: User[] = [];
  data: User[]=[];
constructor(private UserserviceService: UserserviceService){
  this.data=this.UserserviceService.getUData();

}
  loginform=new FormGroup({

    username: new FormControl(''),
    password:new FormControl(''),


  })
  Loginuser(){
    const user  = this.data.find(u=>u.username==this.loginform.get('username')?.value &&  u.password==this.loginform.get('password')?.value);
    if(user) {
      console.log("success");

    }
    else{
      console.log("fail");
    }


  }
}
<!DOCTYPE html>
<html>
<head>
</head>
<body>
  <div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <h1 class="text-center">LogIn</h1>
        <hr/>
    <form  [formGroup]="loginform" (ngSubmit)="Loginuser()">
      <div class="form-group">
        <label for="username">Username:</label>
        <input type="email" class="form-control" formControlName="username" name="username" placeholder="Enter your username">
      </div>
      <div class="form-group">
        <label for="password">Password:</label>
        <input type="password" class="form-control" formControlName="password" name="password" placeholder="Enter your password">
      </div>
      <br>
      <button type="submit" class="btn btn-primary">Login</button>
    </form>
  </div>
  </div>
  </div>
</body>
</html>
