import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import {  OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductServiceService } from '../../Services/product-service.service';
import { Category } from '../../data/category';
import { ignoreElements } from 'rxjs';



@Component({
  selector: 'app-createcat',
  templateUrl: './createcat.component.html',
  styleUrls: ['./createcat.component.css']
})
export class CreatecatComponent implements OnInit {
  public categoryForm: any;
  public catname?: string="";
  public Category?:Category | undefined;
  public id:number=0;

  constructor(private ProductServiceService:ProductServiceService,public router:Router,public route:ActivatedRoute,private formBuilder: FormBuilder){
    this.route.paramMap.subscribe(params =>{
      this.id=Number( params.get('id'));
    })
      if(this.id!==0){
      this.Category=this.ProductServiceService.getcategorybyId(this.id);
      if(this.Category!==undefined){
        this.catname=this.Category.CategoryName;
      }
    }
  }
  ngOnInit(): void {

    this.categoryForm = this.formBuilder.group({
      name: [this.catname, Validators.required]
    });
  }
  onSubmit() {
    if (this.categoryForm.invalid) {
      return;
    }
  }


}
<div class="container border-5 bg-gradient">
  <div class="container p-5 m-3 border-dark">
    <form [formGroup]="categoryForm" (ngSubmit)="onSubmit()">
      <div class="border p-3">
        <div class="form-group row">
          <h2 class="text-info pl-3">Create Category</h2>
        </div>
        <br>
        <div class="row">
          <div class="col-8">
            <div class="form-group row">
              <div class="col-4">
                <label for="Name">Name</label>
              </div>
              <div class="col-8">
                <input class="form-control" type="text" formControlName="name" />
                <br>
              </div>
              <br />
            </div>
            <div class="form-group row">
            </div>
            <div class="form-group row">
              <div class="col-8 offset-4 row">
                <div class="col">
                  <input type="submit" class="btn btn-info w-100" value="Create" [disabled]="categoryForm.invalid" />
                </div>
                <div class="col">
                  <a class="btn btn-success w-100" [routerLink]="['/displaycat']">
                    <i class="fas fa-sign-out-alt"></i> Back
                  </a>
                </div>
              </div>
            </div>
          </div>
          <div class="col-4"></div>
        </div>
      </div>
    </form>
  </div>
</div>
